package JavaBasics;

public class DataTypes {

	//Starting/execution point of the program
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Premitive data type (int,double,char , boolean)
		//1. int
		
		int i= 10; //variable name should be unique
		//int i= 20; not allowed 
		System.out.println(i);
		
		i=20; //it is allowed , i value replacing by 20 
		int j =30;
		System.out.println(j);
		
		//2. double
		double d = 10.12;
		double d1 = 10; // value store 10.00
		
		//3 . char
		
		char c = 'a';
		System.out.println(c);
		char c1= '!';
		char c2 = '>';
		char c3 = '1';
		//char c4 = 'aa'; //not allowed , it should be single digit value 
		
		//4. boolean 
		
		boolean b1= true;
		boolean b2 = false;
		
		//5. string is a classs , not a data type
		
		String s = "test string";
		
		

	}

}
